num_swi = int(input())swi = list(map(int,input().split(' ')))num_stu = int(input())stu = []i = 1for i in range(num_stu):    stu.append(list(map(int, input().split(' '))))    get_num = stu[i][1]    #남자 일때    if stu[i][0] == 1:        j = 1        while(j*get_num<=num_swi):            if swi[j*get_num-1] == 0:                swi[j*get_num-1] = 1            else:                swi[j*get_num-1] = 0            j+=1            #여자일때        else:        if swi[get_num-1] == 1:            swi[get_num-1] = 0        else:            swi[get_num-1] = 1        j=1            while((get_num-j>0)&((get_num+j)<=num_swi)):            if swi[get_num-j-1] == swi[get_num+j-1]:                if swi[get_num-j-1] == 0:                    swi[get_num-j-1] = 1                    swi[get_num+j-1] = 1                else:                    swi[get_num-j-1] = 0                    swi[get_num+j-1] = 0                j+=1            else:                breakfor i in range((num_swi//20)+1):    print(str(swi[i*20:(i+1)*20])[1:-1].replace(',',''))    